package week1.day2;

public class ConvertANegativeNumberToPositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = -40;
		
		if(number<0)
		{
			System.out.println("Negative number "+number+" is converted to Positive Number= "+number*-1);
		}

	}

}
